mapper.select:
SELECT count(*)
FROM t_code
WHERE code = #{code}
AND addr = #{addr}
AND status = 0
AND free = ""

mapper.update:
UPDATE t_code
SET status = #{status}
	, free = #{free}
WHERE code = #{code}
AND addr = #{addr}

mapper.select:
SELECT code, addr, status, free
FROM t_code
WHERE addr = #{addr}
AND status = 0
AND free IN (
	SELECT MIN(free)
	FROM t_code
	WHERE addr = #{addr}
	AND status = 0
)
