mapper.select:
SELECT datee, card, nine, ten, eleven, twelve, thirteen, fourteen, fifteen, sixteen, seventeen, eighteen, nineteen
FROM t_echarts
WHERE datee = #{datee}
AND card = #{card}

mapper.update:
UPDATE t_echarts
SET nine = #{nine}, ten = ${ten}, eleven = #{eleven}, twelve = #{tewlve}
	, thirteen = #{thirteen}, fourteen = #{fourteen}, fifteen = #{fifteen}, sixteen = #{sixteen}
	, seventeen = #{seventeen}, eighteen = #{eighteen}, nineteen #{nineteen}
WHERE datee = #{datee}
AND card = #{card}

mapper.insert:
