mapper.insert:
INSERT INTO t_send (
	from_name, from_tel, from_addr
	, to_name, to_tel, to_addr, price
	, id, org, status, dt
) VALUES (
	#{from_name}, #{from_tel}, #{from_addr}
	, #{to_name}, #{to_tel}, #{to_addr}, #{price}
	, #{id}, #{org}, #{status}, #{dt}
)

mapper.update:
UPDATE t_send
SET status = #{status}
	, dt = #{dt}
WHERE id = #{id}

mapper.select:
SELECT count(*)
FROM t_send
WHERE status = #{status}
AND from_tel IN (
	SELECT phone
	FROM t_user
	WHERE card = #{card}
)

SELECT count(*)
FROM t_send
WHERE status = #{status}
AND org = #{org}
