mapper.select:
SELECT count(*)
FROM t_user
WHERE card = #{card}
AND password = #{password}

SELECT count(*)
FROM t_user
WHERE card = #{card}
AND phone = #{phone}

SELECT t.card, t.password, t.phone, t.name, t.addr, t.count, t.mail
FROM t_user t
WHERE card = #{card}

SELECT t.card, t.password, t.phone, t.name, t.addr, t.count, t.mail
FROM t_user t
WHERE phone = #{phone}

mapper.insert:
INSERT INTO t_user (
	card, password, phone, name, addr, count, mail
) VALUES (
	#{card}, #{password}, #{phone}, #{name}, #{addr}, #{count}, #{mail}
)

mapper.update:
UPDATE t_user
SET password = #{password}
WHERE card = #{card}

UPDATE t_user
SET password = #{newPwd}
WHERE card = #{card}
AND password = #{oldPwd}

UPDATE t_user
SET name = #{name}
	, addr = #{addr}
	, mail = #{mail}
WHERE card = #{card}
